import http.client
import urllib.parse

class Http4j:
    def __init__(self, url, username, password):
        self.url = url
        self.username = username
        self.password = password

    def get(self):
        auth = urllib.parse.urlencode({'username': self.username, 'password': self.password})
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        req = http.client.HTTPConnection(self.url)
        req.request('GET', '/', headers=headers, body=auth)
        return req.getresponse()

    def post(self, data):
        auth = urllib.parse.urlencode(data)
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        req = http.client.HTTPConnection(self.url)
        req.request('POST', '/', headers=headers, body=auth)
        return req.getresponse()

# Usage
