def get_sensitive_info(url, username, password):
    try:
        session = requests.Session()
        session.auth = (username, password)
        response = session.get(url)
        if response.status_code == 200:
            return response.text
        else:
            return "Failed to retrieve sensitive info. Status code: " + str(response.status_code)
