import numpy as np

class DecisionTreeClassifier:
    def __init__(self, max_depth=None):
        self.max_depth = max_depth
        self.tree = {}

    def fit(self, X, y):
        self.tree = self._build_tree(X, y)

    def predict(self, X):
        return [self._predict(inputs) for inputs in X]

    def _build_tree(self, X, y, depth=0):
        if (depth >= self.max_depth):
            leaf_value = np.mean(y)
            return leaf_value

        feat, col = self._best_split(X, y)
        if feat is None:
            return np.mean(y)

        left_X, left_y = X[:, feat < col], y[X[:, feat < col]
        right_X, right_y = X[:, feat >= col], y[X[:, feat >= col]]

        left_tree = self._build_tree(left_X, left_y, depth + 1)
        right_tree = self._build_tree(right_X, right_y, depth + 1)

        return {
            feat,
            {
                "left": left_tree,
                "right": right_tree
            }
        }

    def _best_split(self, X, y):
        m = X.shape[1]
        best_diff = 0
        best_feat = 0
        for i in range(m):
            feat_values = X[:, i]
            unique_values = np.unique(feat_values)
            for value in unique_values:
                feat = (feat_values == value)
                y_value = y[feat]
                mean_left, mean_right = np.mean(y_value[feat]), np.mean(y_value[~feat])
                diff = np.abs(mean_left - mean_right)
                if diff > best_diff:
                    best_diff = diff
                    best_feat = i
        return best_feat, best_feat

    def _predict(self, inputs):
        node = self.tree
        for key in inputs:
            if isinstance(node, dict):
                if inputs[key] < node.keys()[0]:
                    node = node["left"]
                else:
                    node = node["right"]
            else:
                if inputs[key] != node:
                    return node
        return node

